import timedef timeit(func):    def wrapper(*args , **kwargs):        starttime = time.time()        value = func(*args , **kwargs)        endtime = time.time()        print(f"name:{func.__name__} taketime:{endtime-starttime}")        return value    return wrapper@timeitdef view1(request):    for i in range(0, 100):        pass        @timeitdef view2(request):    for i in range(0, 10000):        pass    @timeitdef view3(request):    for i in range(0, 2000000):        pass        view1(1)view2(1)view3(1)